<?php
 use Tygh\Registry; if (!defined('BOOTSTRAP')) { die('Access denied'); } function fn_settings_variants_addons_sd_amazon_products_default_shipping() { $data = array(); $company_id = Registry::ifGet('runtime.company_id', null); foreach (fn_get_available_shippings($company_id) as $shipping) { $data[$shipping['shipping_id']] = $shipping['shipping']; } return $data; } function fn_settings_variants_addons_sd_amazon_products_default_category() { $categories = array(); $categories_list = fn_get_plain_categories_tree(0, false); if (!empty($categories_list)) { $categories[] = '--'; foreach ($categories_list as $category) { if (empty($category['store'])) { $count = empty($category['level']) ? 0 : $category['level'] - 1; $categories[$category['category_id']] = str_repeat('   |   ', $count) . $category['category']; } } } return $categories; } function fn_settings_variants_addons_sd_amazon_products_unshipped_status() { return fn_get_order_statuses_for_addon_setting(); } function fn_settings_variants_addons_sd_amazon_products_shipped_status() { return fn_get_order_statuses_for_addon_setting(); } function fn_settings_variants_addons_sd_amazon_products_canceled_status() { return fn_get_order_statuses_for_addon_setting(); } function fn_get_order_statuses_for_addon_setting() { $data = array( '' => ' -- ' ); foreach (fn_get_statuses(STATUSES_ORDER) as $status) { $data[$status['status']] = $status['description']; } return $data; } 